import React from "react";

export default function ScoreBoard(props) {
  const darkMode = props.darkMode
  const score = props.score
  return (
    <div
      className="score-board"
      style={darkMode ? { color: "white" } : { color: "black" }}
    >
      <h2>Score : {score}</h2>
    </div>
  );
}
